import React from 'react';
import './Calculator.css';

class Calculator extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            display: '',
            currentFormula:'',
            currentNumber:'',
            result: '',
        };
    };

    handleEvaluate = (e) => {
        e.preventDefault();
        const input = e.target.value;
        const numbers = /\d/;
        if (numbers.test(input) | input === '.') {
            this.handleNumbers(e.target.value);
        } else {
            switch(e.target.value){
                case "=":
                    this.handleResult();
                // case "C":
                //     break;
                // case "ON":
                //     break;
                default:
                    this.handleFormula(e.target.value);
                    break;
            };
        };
    };

    handleResult = () => {
        const formula = this.state.currentFormula;
        const regexp = /\/d/
        let arr = formula.split(regexp)
        console.log(arr);
    };

    clear = () => {

    };

    handleOn = () => {

    };

    handleNumbers = (value) => {
        const numbers = /\d/;
        const dot = /\./;
        let currentNumber = this.state.currentNumber
        let currentFormula = this.state.currentFormula
        if (numbers.test(value)){
            currentNumber += value
            currentFormula += value
        } else { 
            if(!dot.test(currentNumber)){
                currentNumber += value
                currentFormula += value
            };
        };
        this.setState({
            currentNumber,
            currentFormula,
            display: currentNumber
        });
    };

    handleFormula = (value) => {
        const currentFormula = this.state.currentFormula + value;
        this.setState({
            currentFormula,
            currentNumber: '',
            display: value
        });
    };

    render(){
        return(<div id="inner">

        <div id="calculator" >

        <div id="calculator-display" className="col">
            <div>{this.state.currentFormula}</div>
            <div>{this.state.display}</div>
        </div>

            <div id="keys">

                <div id="row-1" className="row">
                    <button onClick={this.handleEvaluate} id="seven" className="button" value="7">7</button>
                    <button onClick={this.handleEvaluate} id="eight" className="button" value="8">8</button>
                    <button onClick={this.handleEvaluate} id="nine" className="button" value="9">9</button>
                    <button onClick={this.handleEvaluate} id="multiply" className="button button-2" value="*">x</button>
                    <button onClick={this.handleEvaluate} id="pourcentage" className="button button-2" value="%">%</button>
                </div>

                <div id="row-2" className="row">
                    <button onClick={this.handleEvaluate} id="four" className="button" value="4">4</button>
                    <button onClick={this.handleEvaluate} id="five" className="button" value="5">5</button>
                    <button onClick={this.handleEvaluate} id="six" className="button" value="6">6</button>
                    <button onClick={this.handleEvaluate} id="substract" className="button button-2" value="-">-</button>
                    <button onClick={this.handleEvaluate} id="divide" className="button button-2" value="/">/</button>  
                </div>

                <div id="row-3" className="row">
                    <button onClick={this.handleEvaluate} id="one" className="button" value="1">1</button>
                    <button onClick={this.handleEvaluate} id="two" className="button" value="2">2</button>
                    <button onClick={this.handleEvaluate} id="three" className="button" value="3">3</button>
                    <button onClick={this.handleEvaluate} id="add" className="button button-3" value="+">+</button>
                    <button onClick={this.handleEvaluate} id="clear" className="button button-3" value="C">C</button>
                </div>

                <div id="row-4" className="row">
                    <button onClick={this.handleEvaluate} id="zero" className="button" value="0">0</button>
                    <button onClick={this.handleEvaluate} id="decimal" className="button" value=".">.</button>
                    <button onClick={this.handleEvaluate} id="equal" className="button" value="=">=</button>
                    <button onClick={this.handleEvaluate} id="on" className="button button-3" value="ON">ON</button>
                </div>


            </div>

        </div>

        </div>);
    };
};

export default Calculator;
