    };

    render(){
        return(<div id="inner">

        <div id="calculator" className="container">

            <div className="row">
                <div id="calculator-display" className="col-12">Display</div>
            </div>

            <div className="row">
                <button id="seven" className="col">7</button>
                <button id="eight" className="col">8</button>
                <button id="nine" className="col">9</button>
                <button id="multiply" className="col">x</button>
                <button id="pourcentage" className="col">%</button>
            </div>

            <div className="row">
                <button id="four" className="col">4</button>
                <button id="five" className="col">5</button>
                <button id="six" className="col">6</button>
                <button id="substract" className="col">-</button>
                <button id="divide" className="col">รท</button>
            </div>

            <div className="row">
                <button id="one" className="col">1</button>
                <button id="two" className="col">2</button>
                <button id="three" className="col">3</button>
            </div>

            <div className="row">
                <button id="zero" className="col">0</button>
                <button id="decimal" className="col">.</button>
                <button id="equal" className="col">=</button>
            </div>

        </div>

        </div>);
    };
};

export default Calculator;
