import React from 'react';
import './Calculator.css';

class Calculator extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            input: ''
        };
    };

    handleClick = (e) => {

    };

    render(){
        return(

            <div id="container-fluid">

            <div class="container">
                <div class="row">
                    <div class="col-lg-3">3 colonnes</div>
                    <div class="col-lg-6">6 colonnes</div>
                    <div class="col-lg-3">3 colonnes</div>
                </div>
                <div class="row">
                    <div class="col-lg-3">3 colonnes</div>
                    <div class="col-lg-offset-6 col-lg-3">3 colonnes</div>
                </div>
            </div>

                <div className="row">
                    <div id="calculator-display" className="col-sm-10"></div>
                </div>

                <div id="container-keys">

                    <div className="row">
                        <button id="seven" className="col-lg-offset-1 col-lg-2">7</button>
                        <button id="eight" className="col-lg-2">8</button>
                        <button id="nine" className="col-lg-2">9</button>
                        <button id="multiply" className="col-lg-2">x</button>
                        <button id="pourcentage" className="col-lg-2 col-lg-offset-1">%</button>
                    </div>

                    <div className="row">
                            <button id="four" className="col-sm-2">4</button>
                            <button id="five" className="col-sm-2">5</button>
                            <button id="six" className="col-sm-2">6</button>
                            <button id="substract" className="col-sm-2">-</button>
                            <button id="divide" className="col-sm-2">÷</button>
                    </div>

                    <div className="row">
                        <button id="one" className="col-sm-2">1</button>
                        <button id="two" className="col-sm-2">2</button>
                        <button id="three" className="col-sm-2">3</button>
                    </div>

                    <div className="row">
                        <button id="zero" className="col-sm-2">0</button>
                        <button id="decimal" className="col-sm-2">.</button>
                        <button id="equal" className="col-sm-2">=</button>
                    </div>

                </div>

                <div id="operators">
                    <button id="add" className="operators-button">+</button>

                    <button id="multiply" className="operators-button">x</button>

                </div>

                <div id="other-buttons">
                    <button id="decimal" className="other-buttons">.</button>
                    <button id="clear" className="other-buttons">AC</button>
                    <button id="equal" className="equal-button">=</button>
                </div>

            </div>
        );
    };
};

export default Calculator;
