import React from 'react';
import './Calculator.css';

class Calculator extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            input: ''
        };
    };

    handleClick = (e) => {
        console.log(e)
        console.log(e.target.value)
    };

    render(){
        return(<div id="inner">

        <div id="calculator" >

            <div id="calculator-display" className="col"></div>

            <div id="keys">

                <div id="row-1" className="row">
                    <button onClick={this.handleClick} id="seven" value="7" className="button">7</button>
                    <button onClick={this.handleClick} id="eight" className="button">8</button>
                    <button onClick={this.handleClick} id="nine" className="button">9</button>
                    <button onClick={this.handleClick} id="multiply" className="button button-2">x</button>
                    <button onClick={this.handleClick} id="pourcentage" className="button button-2">%</button>
                </div>

                <div id="row-2" className="row">
                    <button onClick={this.handleClick} id="four" className="button">4</button>
                    <button onClick={this.handleClick} id="five" className="button">5</button>
                    <button onClick={this.handleClick} id="six" className="button">6</button>
                    <button onClick={this.handleClick} id="substract" className="button button-2">-</button>
                    <button onClick={this.handleClick} id="divide" className="button button-2">/</button>  
                </div>

                <div id="row-3" className="row">
                    <button onClick={this.handleClick} id="one" className="button">1</button>
                    <button onClick={this.handleClick} id="two" className="button">2</button>
                    <button onClick={this.handleClick} id="three" className="button">3</button>
                    <button onClick={this.handleClick} id="add" className="button button-3">+</button>
                    <button onClick={this.handleClick} id="clear" className="button button-3">C</button>
                </div>

                <div id="row-4" className="row">
                    <button onClick={this.handleClick} id="zero" className="button">0</button>
                    <button onClick={this.handleClick} id="decimal" className="button">.</button>
                    <button onClick={this.handleClick} id="equal" className="button">=</button>
                    <button onClick={this.handleClick} id="on" className="button button-3">ON</button>
                </div>


            </div>

        </div>

        </div>);
    };
};

export default Calculator;
