import React from 'react';
import './Calculator.css';

class Calculator extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            input: '',
            formula: ''
        };
    };

    handleClick = (e) => {
        e.preventDefault();
        const input =this.state.input + ' ' + e.target.value;
        this.setState({input});
    };

    render(){
        return(<div id="inner">

        <div id="calculator" >

        <div id="calculator-display" className="col">{this.state.input}</div>

            <div id="keys">

                <div id="row-1" className="row">
                    <button onClick={this.handleClick} id="seven" className="button" value="7">7</button>
                    <button onClick={this.handleClick} id="eight" className="button" value="8">8</button>
                    <button onClick={this.handleClick} id="nine" className="button" value="9">9</button>
                    <button onClick={this.handleClick} id="multiply" className="button button-2" value="*">x</button>
                    <button onClick={this.handleClick} id="pourcentage" className="button button-2" value="%">%</button>
                </div>

                <div id="row-2" className="row">
                    <button onClick={this.handleClick} id="four" className="button" value="4">4</button>
                    <button onClick={this.handleClick} id="five" className="button" value="5">5</button>
                    <button onClick={this.handleClick} id="six" className="button" value="6">6</button>
                    <button onClick={this.handleClick} id="substract" className="button button-2" value="-">-</button>
                    <button onClick={this.handleClick} id="divide" className="button button-2" value="/">/</button>  
                </div>

                <div id="row-3" className="row">
                    <button onClick={this.handleClick} id="one" className="button" value="1">1</button>
                    <button onClick={this.handleClick} id="two" className="button" value="2">2</button>
                    <button onClick={this.handleClick} id="three" className="button" value="3">3</button>
                    <button onClick={this.handleClick} id="add" className="button button-3" value="+">+</button>
                    <button onClick={this.handleClick} id="clear" className="button button-3" value="C">C</button>
                </div>

                <div id="row-4" className="row">
                    <button onClick={this.handleClick} id="zero" className="button" value="0">0</button>
                    <button onClick={this.handleClick} id="decimal" className="button" value=".">.</button>
                    <button onClick={this.handleClick} id="equal" className="button" value="=">=</button>
                    <button onClick={this.handleClick} id="on" className="button button-3" value="ON">ON</button>
                </div>


            </div>

        </div>

        </div>);
    };
};

export default Calculator;
